class Proyecto_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get proyecto by cod_proy
     */
    function get_proyecto($cod_proy)
    {
        $proyecto = $this->db->query("
            SELECT
                *

            FROM
                `proyecto`

            WHERE
                `cod_proy` = ?
        ",array($cod_proy))->row_array();

        return $proyecto;
    }
        
    /*
     * Get all proyecto
     */
    function get_all_proyecto()
    {
        $proyecto = $this->db->query("
            SELECT
                *

            FROM
                `proyecto`

            WHERE
                1 = 1

            ORDER BY `cod_proy` DESC
        ")->result_array();

        return $proyecto;
    }
        
    /*
     * function to add new proyecto
     */
    function add_proyecto($params)
    {
        $this->db->insert('proyecto',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update proyecto
     */
    function update_proyecto($cod_proy,$params)
    {
        $this->db->where('cod_proy',$cod_proy);
        return $this->db->update('proyecto',$params);
    }
    
    /*
     * function to delete proyecto
     */
    function delete_proyecto($cod_proy)
    {
        return $this->db->delete('proyecto',array('cod_proy'=>$cod_proy));
    }
}
