class Recorrido_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get recorrido by recorrido_id
     */
    function get_recorrido($recorrido_id)
    {
        $recorrido = $this->db->query("
            SELECT
                *

            FROM
                `recorrido`

            WHERE
                `recorrido_id` = ?
        ",array($recorrido_id))->row_array();

        return $recorrido;
    }

    function recorrido_dist($filtro)
  {
        $recorrido = $this->db->query("
            SELECT
                v.*, c.*,u.*, t.tiporespuesta_descripcion, p.pedido_total
            FROM
                recorrido_usuario v 
            
            LEFT JOIN cliente c on v.cliente_id=c.cliente_id
            LEFT JOIN pedido p on v.pedido_id=p.pedido_id
            LEFT JOIN usuario u on v.usuario_id=u.usuario_id
            LEFT JOIN tipo_respuesta t on v.tiporespuesta_id=t.tiporespuesta_id

            WHERE
            1=1
            ".$filtro."
            ORDER BY v.recorrido_id
            
        ")->result_array();

        return $recorrido;
  }
    
    /*
     * Get all recorrido count
     */
    function get_all_recorrido_count()
    {
        $recorrido = $this->db->query("
            SELECT
                count(*) as count

            FROM
                `recorrido`
        ")->row_array();

        return $recorrido['count'];
    }
        
    /*
     * Get all recorrido
     */
   
    /*
     * function to add new recorrido
     */
    function add_recorrido($params)
    {
        $this->db->insert('recorrido',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update recorrido
     */
    function update_recorrido($recorrido_id,$params)
    {
        $this->db->where('recorrido_id',$recorrido_id);
        return $this->db->update('recorrido',$params);
    }
    
    /*
     * function to delete recorrido
     */
    function delete_recorrido($recorrido_id)
    {
        return $this->db->delete('recorrido',array('recorrido_id'=>$recorrido_id));
    }
}