class Red_social_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get all red_social
     */
    function get_all_red_social()
    {
        $red_social = $this->db->query("
            SELECT
                rs.*, e.estado_descripcion, e.estado_color
            FROM
                `red_social` rs
            left join estado e on rs.estado_id = e.estado_id
            ORDER BY rs.`redsocial_id` ASC
        ")->result_array();
        return $red_social;
    }
    
    /*
     * function to add new red_social
     */
    function add_red_social($params)
    {
        $this->db->insert('red_social',$params);
        return $this->db->insert_id();
    }
    
    /*
     * Get red_social by redsocial_id
     */
    function get_red_social($redsocial_id)
    {
        $red_social = $this->db->query("
            SELECT
                *
            FROM
                `red_social`
            WHERE
                `redsocial_id` = ?
        ",array($redsocial_id))->row_array();
        return $red_social;
    }
    
    /*
     * function to update red_social
     */
    function update_red_social($redsocial_id,$params)
    {
        $this->db->where('redsocial_id',$redsocial_id);
        return $this->db->update('red_social',$params);
    }
    /*
     * Get all red_social a todos los activos
     */
    function get_all_red_socialactivo()
    {
        $red_social = $this->db->query("
            SELECT
                rs.*, e.estado_descripcion, e.estado_color
            FROM
                `red_social` rs
            left join estado e on rs.estado_id = e.estado_id
            WHERE
                rs.estado_id = 1
            ORDER BY rs.`redsocial_id` ASC
        ")->result_array();
        return $red_social;
    }
    
}
