<?php
/* 
 * Generated by CRUDigniter v3.2 
 * www.crudigniter.com
 */
 
class Responsable_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get responsable by responsable_id
     */
    function get_responsable($responsable_id)
    {
        $responsable = $this->db->query("
            SELECT
                *

            FROM
                `responsable`

            WHERE
                `responsable_id` = ?
        ",array($responsable_id))->row_array();

        return $responsable;
    }
    
    /*
     * Get all responsable count
     */
    function get_all_responsable_count()
    {
        $responsable = $this->db->query("
            SELECT
                count(*) as count

            FROM
                `responsable`
        ")->row_array();

        return $responsable['count'];
    }
        
    /*
     * Get all responsable
     * y se modifico la consulta para que muestre con su estado mas MEV
     */
    function get_all_responsable($params = array())
    {
        $limit_condition = "";
        if(isset($params) && !empty($params))
            $limit_condition = " LIMIT " . $params['offset'] . "," . $params['limit'];
        
        $responsable = $this->db->query("
            SELECT
                *

            FROM
                responsable r, estado e

            WHERE
                r.estado_id = e.estado_id

            ORDER BY `responsable_id` DESC

            " . $limit_condition . "
        ")->result_array();

        return $responsable;
    }
        
    /*
     * function to add new responsable
     */
    function add_responsable($params)
    {
        $this->db->insert('responsable',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update responsable
     */
    function update_responsable($responsable_id,$params)
    {
        $this->db->where('responsable_id',$responsable_id);
        return $this->db->update('responsable',$params);
    }
    
    /*
     * function to delete responsable
     */
    function delete_responsable($responsable_id)
    {
        return $this->db->delete('responsable',array('responsable_id'=>$responsable_id));
    }
    
    /*
     * selecciona a todos los responsables cin el id ==1 (ACTIVOS)
     */
    function get_all_responsable_id1()
    {
        $responsable = $this->db->query("
            SELECT
                *

            FROM
                responsable r, estado e

            WHERE
                r.estado_id = e.estado_id
                and e.estado_id = 1

            ORDER BY `responsable_id` DESC
        ")->result_array();

        return $responsable;
    }
}
