class Seccion_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get seccion by seccion_id
     */
    function get_seccion($seccion_id)
    {
        $seccion = $this->db->query("
            SELECT
                *

            FROM
                `seccion`

            WHERE
                `seccion_id` = ?
        ",array($seccion_id))->row_array();

        return $seccion;
    }
    
    /*
     * Get all seccion count
     */
    function get_all_seccion_count()
    {
        $seccion = $this->db->query("
            SELECT
                count(*) as count

            FROM
                `seccion`
        ")->row_array();

        return $seccion['count'];
    }
        
    /*
     * Get all seccion
     */
    function get_all_seccion($params = array())
    {
        $limit_condition = "";
        if(isset($params) && !empty($params))
            $limit_condition = " LIMIT " . $params['offset'] . "," . $params['limit'];
        
        $seccion = $this->db->query("
            SELECT
                *

            FROM
                seccion s, pagina_web p, estado_pagina e

            WHERE
                s.pagina_id = p.pagina_id
                and s.estadopag_id = e.estadopag_id

            ORDER BY `seccion_id` DESC

            " . $limit_condition . "
        ")->result_array();

        return $seccion;
    }
        
    /*
     * function to add new seccion
     */
    function add_seccion($params)
    {
        $this->db->insert('seccion',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update seccion
     */
    function update_seccion($seccion_id,$params)
    {
        $this->db->where('seccion_id',$seccion_id);
        return $this->db->update('seccion',$params);
    }
    
    /*
     * function to delete seccion
     */
    function delete_seccion($seccion_id)
    {
        return $this->db->delete('seccion',array('seccion_id'=>$seccion_id));
    }
}
