 */
 
class Sistema_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get sistema by sistema_id
     */
    function get_sistema()
    {
        return $this->db->get_where('sistema',array('sistema_id'=>1))->row_array();
    }
        
    /*
     * Get all sistema
     */
    function get_all_sistema()
    {
        $sistema = $this->db->query("
            SELECT
                *

            FROM
                sistema

            WHERE
                1 = 1
                
                ORDER BY sistema_id DESC

        ")->result_array();

        return $sistema;
    }
        
    /*
     * function to add new sistema
     */
    function add_sistema($params)
    {
        $this->db->insert('sistema',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update sistema
     */
    function update_sistema($sistema_id,$params)
    {
        $this->db->where('sistema_id',$sistema_id);
        return $this->db->update('sistema',$params);
    }
    
    /*
     * function to delete sistema
     */
    function delete_sistema($sistema_id)
    {
        return $this->db->delete('sistema',array('sistema_id'=>$sistema_id));
    }
    /*
     * Get all sistema
     */
    function get_sistema_usada($nombre_sistema)
    {
        $sistema = $this->db->query("
            SELECT
                u.*
            FROM
                sistema u
            left join producto p on u.sistema_nombre = p.producto_sistema
            where
                p.producto_sistema = '".$nombre_sistema."'

        ")->result_array();

        return $sistema;
    }

    function truncate_table(){
        $this->db->query("TRUNCATE sistema");
    }
}
