 */
 
class Slide_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get slide by slide_id
     */
    function get_slide($slide_id)
    {
        $slide = $this->db->query("
            SELECT
                *

            FROM
                `slide`

            WHERE
                `slide_id` = ?
        ",array($slide_id))->row_array();

        return $slide;
    }
    
    /*
     * Get all slide count
     */
    function get_all_slide_count()
    {
        $slide = $this->db->query("
            SELECT
                count(*) as count

            FROM
                `slide`
        ")->row_array();

        return $slide['count'];
    }
        
    /*
     * Get all slide
     */
    function get_all_slide($params = array())
    {
        $limit_condition = "";
        if(isset($params) && !empty($params))
            $limit_condition = " LIMIT " . $params['offset'] . "," . $params['limit'];
        
        $slide = $this->db->query("
            SELECT
                *

            FROM
                slide s, estado_pagina e, pagina_web p

            WHERE
                s.estadopag_id = e.estadopag_id
                and s.pagina_id = p.pagina_id

            ORDER BY `slide_id` DESC

            " . $limit_condition . "
        ")->result_array();

        return $slide;
    }
        
    /*
     * function to add new slide
     */
    function add_slide($params)
    {
        $this->db->insert('slide',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update slide
     */
    function update_slide($slide_id,$params)
    {
        $this->db->where('slide_id',$slide_id);
        return $this->db->update('slide',$params);
    }
    
    /*
     * function to delete slide
     */
    function delete_slide($slide_id)
    {
        return $this->db->delete('slide',array('slide_id'=>$slide_id));
    }
}
