class Submenu_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get submenu by submenu_id
     */
    function get_submenu($submenu_id)
    {
        $submenu = $this->db->query("
            SELECT
                *

            FROM
                `submenu`

            WHERE
                `submenu_id` = ?
        ",array($submenu_id))->row_array();

        return $submenu;
    }
    
    /*
     * Get all submenu count
     */
    function get_all_submenu_count()
    {
        $submenu = $this->db->query("
            SELECT
                count(*) as count

            FROM
                `submenu`
        ")->row_array();

        return $submenu['count'];
    }
        
    /*
     * Get all submenu
     */
    function get_all_submenu($params = array())
    {
        $limit_condition = "";
        if(isset($params) && !empty($params))
            $limit_condition = " LIMIT " . $params['offset'] . "," . $params['limit'];
        
        $submenu = $this->db->query("
            SELECT
                *

            FROM
                submenu s, estado_pagina e, menu m

            WHERE
                s.estadopag_id = e.estadopag_id
                and s.menu_id = m.menu_id

            ORDER BY `submenu_id` DESC

            " . $limit_condition . "
        ")->result_array();

        return $submenu;
    }
        
    /*
     * function to add new submenu
     */
    function add_submenu($params)
    {
        $this->db->insert('submenu',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update submenu
     */
    function update_submenu($submenu_id,$params)
    {
        $this->db->where('submenu_id',$submenu_id);
        return $this->db->update('submenu',$params);
    }
    
    /*
     * function to delete submenu
     */
    function delete_submenu($submenu_id)
    {
        return $this->db->delete('submenu',array('submenu_id'=>$submenu_id));
    }
}
