    /*
     * Get tabla_asiento by cod_tabla_asiento
     */
    function get_tabla_asiento($cod_tabla_asiento)
    {
        $tabla_asiento = $this->db->query("
            SELECT
                *

            FROM
                `tabla_asiento`

            WHERE
                `cod_tabla_asiento` = ?
        ",array($cod_tabla_asiento))->row_array();

        return $tabla_asiento;
    }
        
    /*
     * Get all tabla_asiento
     */
    function get_all_tabla_asiento()
    {
        $tabla_asiento = $this->db->query("
            SELECT
                *

            FROM
                `tabla_asiento`

            WHERE
                1 = 1

            ORDER BY `cod_tabla_asiento` DESC
        ")->result_array();

        return $tabla_asiento;
    }
        
    /*
     * function to add new tabla_asiento
     */
    function add_tabla_asiento($params)
    {
        $this->db->insert('tabla_asiento',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update tabla_asiento
     */
    function update_tabla_asiento($cod_tabla_asiento,$params)
    {
        $this->db->where('cod_tabla_asiento',$cod_tabla_asiento);
        return $this->db->update('tabla_asiento',$params);
    }
    
    /*
     * function to delete tabla_asiento
     */
    function delete_tabla_asiento($cod_tabla_asiento)
    {
