class Tiempo_uso_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get tiempo_uso by tiposerv_id
     */
    function get_tiempo_uso($tiempouso_id)
    {
        return $this->db->get_where('tiempo_uso',array('tiempouso_id'=>$tiempouso_id))->row_array();
    }
        
    /*
     * Get all tiempo_uso
     */
    function get_all_tiempo_uso()
    {
        $tiempo_uso = $this->db->query("
            SELECT
                *

            FROM
                tiempo_uso tu, estado e

            WHERE
                tu.estado_id = e.estado_id
                ORDER BY tu.tiempouso_id DESC

        ")->result_array();

        return $tiempo_uso;
    }
        
    /*
     * function to add new tiempo_uso
     */
    function add_tiempo_uso($params)
    {
        $this->db->insert('tiempo_uso',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update tiempo_uso
     */
    function update_tiempo_uso($tiempouso_id,$params)
    {
        $this->db->where('tiempouso_id',$tiempouso_id);
        return $this->db->update('tiempo_uso',$params);
    }
    
    /*
     * function to delete tiempo_uso
     */
    function delete_tiempo_uso($tiempouso_id)
    {
        return $this->db->delete('tiempo_uso',array('tiempouso_id'=>$tiempouso_id));
    }
    
    /*
     * Get all tiempo_uso con id ==1
     */
    function get_all_tiempo_uso_id1()
    {
        $tiempouso = $this->db->query("
            SELECT
                tu.`tiempouso_id`, tu.`tiempouso_descripcion`

            FROM
                tiempo_uso tu, estado e

            WHERE
                tu.estado_id = e.estado_id
                and e.`estado_tipo` = 1
                and e.estado_id = 1
                
        ")->result_array();

        return $tiempouso;
    }
}
