class Tipo_respuesta_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get tipo_respuesta by categoriacliezona_id
     */
    function get_tipo_respuesta($tiporespuesta_id)
    {
        return $this->db->get_where('tipo_respuesta',array('tiporespuesta_id'=>$tiporespuesta_id))->row_array();
    }
        
    /*
     * Get all tipo_respuesta
     */
    function get_all_tipo_respuesta()
    {
        $tiporespuesta = $this->db->query("
            SELECT
                tr.*

            FROM
                tipo_respuesta tr

            WHERE
                1 = 1
                
                ORDER BY tr.tiporespuesta_descripcion

        ")->result_array();

        return $tiporespuesta;
    }
        
    /*
     * function to add new tipo_respuesta
     */
    function add_tipo_respuesta($params)
    {
        $this->db->insert('tipo_respuesta',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update tipo_respuesta
     */
    function update_tipo_respuesta($tiporespuesta_id,$params)
    {
        $this->db->where('tiporespuesta_id',$tiporespuesta_id);
        return $this->db->update('tipo_respuesta',$params);
    }
    
    /*
     * function to delete tipo_respuesta
     */
    function delete_tipo_respuesta($tiporespuesta_id)
    {
        return $this->db->delete('tipo_respuesta',array('tiporespuesta_id'=>$tiporespuesta_id));
    }
    
}
