class Unidad_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get unidad by unidad_id
     */
    function get_unidad($unidad_id)
    {
        return $this->db->get_where('unidad',array('unidad_id'=>$unidad_id))->row_array();
    }
        
    /*
     * Get all unidad
     */
    function get_all_unidad()
    {
        $unidad = $this->db->query("
            SELECT
                *

            FROM
                unidad

            WHERE
                1 = 1
                
                ORDER BY unidad_id DESC

        ")->result_array();

        return $unidad;
    }
        
    /*
     * function to add new unidad
     */
    function add_unidad($params)
    {
        $this->db->insert('unidad',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update unidad
     */
    function update_unidad($unidad_id,$params)
    {
        $this->db->where('unidad_id',$unidad_id);
        return $this->db->update('unidad',$params);
    }
    
    /*
     * function to delete unidad
     */
    function delete_unidad($unidad_id)
    {
        return $this->db->delete('unidad',array('unidad_id'=>$unidad_id));
    }
    /*
     * Get all unidad
     */
    function get_unidad_usada($nombre_unidad)
    {
        $unidad = $this->db->query("
            SELECT
                u.*
            FROM
                unidad u
            left join producto p on u.unidad_nombre = p.producto_unidad
            where
                p.producto_unidad = '".$nombre_unidad."'

        ")->result_array();

        return $unidad;
    }

    function truncate_table(){
        $this->db->query("TRUNCATE unidad");
    }
}
